import React from "react";

class GetCurrentDate extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          date: new Date()
        };
      }
      
    render () {
    return (
      <div class="date">
        <p><strong> {this.state.date.toLocaleDateString()}</strong></p>
      </div>
    );
    }
}
export default GetCurrentDate;
